package net.todorov;

import net.todorov.Deck;

public class Main {

    public static void main(String[] args) {

        Deck deck = new Deck();
        printDeck(deck,"Default");

        deck.shuffle(100);
        printDeck(deck,"Shuffled");

        deck.sort(Deck.SortType.ASC);
        printDeck(deck,"Sorted:ASC");

        deck.sort(Deck.SortType.DESC);
        printDeck(deck,"Sorted:DESC");
    }

    private static void printDeck(Deck deck, String title) {
        System.out.println(title);
        System.out.println("\t" + deck.toString());

    }
}
